mapper.select:
SELECT COUNT(*)
FROM vote
WHERE vote_date = #{voteDate};

mapper.insert:
INSERT INTO attend(
    vote_id, fb_user_id , attend_status, attend_mod_date
)VALUES (
    (
        SELECT vote_id
        FROM vote
        WHERE vote_date = #{sunday}
    )
    , #{fbUserId}, #{attendStatus}, now()
)

mapper.select:
SELECT attend_status
FROM attend
WHERE 1=1
AND fb_user_id = #{fbUserId}
AND vote_id = (
    SELECT vote_id
    FROM vote
    WHERE vote_date = #{sunday}
)
