mapper.select:
SELECT vote_date
FROM vote
WHERE vote_id = #{voteId};

SELECT fu.fb_user_id, fu.fb_user_name, m.member_ability_avg
FROM fb_user fu
LEFT OUTER JOIN member m
ON fu.fb_user_id = m.fb_user_id
WHERE fu.fb_user_id IN(
    SELECT fb_user_id
    FROM team_member
    WHERE team_id = (
        SELECT team_id
        FROM team
        WHERE 1=1
        AND team_name = #{teamName}
        AND vote_id = #{voteId}
    )
)
ORDER BY fu.fb_user_birth;

SELECT team_name
FROM team
WHERE vote_id = #{voteId}
